import React, { useState } from 'react';
import Datacircle from './Datacircle';
import Navbar from './Navbar';
import Pausebtn from './Pausebtn';
import './index.css'

function Bubblesort(){
    var [Darray,setArray]=useState([0,0,0,0,0,0]);
    return(
        <div>
            <Navbar/>
            <br/>
            <div className='container'>
                <p className='text-secondary'>Ready</p>
            </div>
            <br/><br/><br/><br/>
            <div className='container-fluid'>
                <div className='grid'>
                    <div className="row">
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                        {Darray.map((val,index)=><Datacircle key={index} eval={val}/>)}
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                    </div><br/><br/><br/><br/>
                <div className="row">
                    <div className="col-4"></div>
                    {/* <Restartbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-secondary' onClick={()=>{
                            var da=[];
                            for(var i=0;i<6;i++){
                                da[i]=(Math.floor((Math.random()*100)+1));
                            }
                            setArray(da);
                        }
                    }>reset</button>
                </div>
                    {/* <Startbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-primary' onClick={()=>{
                            let da=Darray;
                            for(var i=0;i<da.length-1;i++){
                                // let max=Darray[0];
                                let temp=0;
                                for(var j=1;j<da.lenght-1-i;j++){
                                    if(da[i]>da[j]) { 
                                        temp=da[i];
                                        da[i]=da[j];
                                        da[j]=temp; 
                                    }
                                    console.log(da);
                                    setArray(da);
                                }
                            }
                        }    
                    }>start</button>
                    </div>
                    <Pausebtn/>
                    <div className="col"></div>
                    
                    </div>
                </div>
            </div>
            <div className='footer'>

            </div>
        </div>
    );
}
export default Bubblesort;
