import React, {useState,useEffect } from 'react';
// import Datacircle from './Datacircle';
// import Navbar from './Navbar';
import Pausebtn from './Pausebtn';
import './index.css'


function Bubblesort(){
    var [Darray,setArray]=useState([{
        id:0,
        data:0,
        bgclr:'white',
    },
    {
        id:1,
        data:0,
        bgclr:'white',
    },
    {
        id:2,
        data:0,
        bgclr:'white',
    },
    {
        id:3,
        data:0,
        bgclr:'white',
    },
    {
        id:4,
        data:0,
        bgclr:'white',
    },
    {
        id:5,
        data:0,
        bgclr:'white',
    }]);
    
    function setval(id,val,clr){
        setArray((preValue)=>{
            if(id===0){
            // console.log(preValue[0]);
            return [
                    {data:val,bgclr:clr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===1){
            // console.log(preValue[0]);
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:val,bgclr:clr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===2){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:val,bgclr:clr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===3){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:val,bgclr:clr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]

            }
            else if(id===4){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:val,bgclr:clr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===5){
            // console.log(preValue);
            return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:val,bgclr:clr,id:preValue[5].id}
                ]
            }
            // console.log(preValue);
        })
    }
    function sleep(ms){
        return new Promise(resolve=>setTimeout(resolve,ms));
    }    
    // var id1;
    // function func1(i,da,clr){
        // clearInterval(id1)
        // setval(i,da,clr)
    // }
    
    // var update=(da)=>{
    //     var interval=setInterval(()=>{
    //         setArray(da);
    //         console.log(da);
    //     },1000);
    //     return()=>clearInterval(interval);
    // }
    // useEffect(()=>{
        
    //     setTimeout(func3,1000);
    //     function func3(){
    //         console.log(Darray);
    //     }
    // });
    return(
        <div>
            
            <div className='container'>
                <p className='text-secondary'>Ready</p>
            </div>
            <br/><br/><br/><br/>
            <div className='container'>
                <div className='grid'>
                    <div className="row">
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                        {Darray.map((value,index)=>{
                            return (<div key={value.id} className='col'>
                            <h1 className='rounded-circle text-center border border-secondary' 
                            id={value.id}
                            style={{backgroundColor:value.bgclr}}>{value.data}</h1>
                            </div>);})
                        }
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                    </div><br/><br/><br/><br/>
                <div className="row">
                    <div className="col-4"></div>
                    {/* <Restartbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-secondary' onClick={()=>{
                            var da=[];
                            var i=0;
                            var id=setInterval(func,100);
                            function func(){
                                if(i>4){
                                    clearInterval(id);
                                }
                                da[i]=(Math.floor((Math.random()*100)+1));
                                setval(i,da[i],"white");
                                i++;
                            }
                        }
                    }>reset</button>
                </div>
                    {/* <Startbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-primary' onClick={async()=>{
                            var da=[Darray[0].data,Darray[1].data,Darray[2].data,Darray[3].data,Darray[4].data,Darray[5].data];
                            console.log(da);
                            var i=0;
                            var id=setInterval(func1,100);
                            function func1(){
                                if(i>4){
                                    clearInterval(id);
                                }
                                setval(i,da[i],"lightblue");
                                i++;
                                return
                            }
                            var k=0;
                            while(k<da.length){
                                let temp=0;
                                var j=1;
                                while(j<da.length-k){
                                    await sleep(2000)
                                    setTimeout(func0(),1000);
                                        function func0(){
                                            setval(j-1,da[j-1],"blue");
                                            return
                                        }
                                    // setTimeout(setval,1000,j-1,da[j-1],"blue");
                                    setTimeout(func01(),1000);
                                        function func01(){
                                            
                                                setval(j,da[j],"blue");
                                                return        
                                        }
                                    // setTimeout(setval,1000,j,da[j],"blue");
                                    
                                    if(da[j-1]>da[j]) {
                                         
                                        temp=da[j-1];
                                        // setTimeout(func2,1000,k,da[k],"blue");
                                        
                                        setTimeout(func2(),1000);
                                        function func2(){
                                            da[j-1]=da[j];
                                            setval(j-1,da[j-1],"blue");
                                            return
                                        }
                                        setTimeout(func3(),1000);
                                        function func3(){
                                            
                                                da[j]=temp;
                                                setval(j,da[j],"blue");
                                                return        
                                        }
                                        
                                        
                                    }
                                    // setTimeout(func4(),10000);
                                    // function func4(){
                                        
                                    //         // da[j]=temp;
                                    //         setval(j-1,da[j-1],"lightblue");
                                    //         // clearInterval(id3)
                                    //         return        
                                    // }
                                    setTimeout(setval,1000,j-1,da[j-1],"lightblue");
                                    // setTimeout(func5(),10000);
