import React, { useState } from 'react';
import Datacircle from './Datacircle';
import Navbar from './Navbar';
import Pausebtn from './Pausebtn';
import './index.css'


function Bubblesort(){
    var [Darray,setArray]=useState([0,0,0,0,0,0]);
    var DA={
        Darray:Darray,
        colors:[white]
    }
    var update=(val)=>{
        setArray(val);
        console.log(val);
    }
    return(
        <div>
            <Navbar/>
            <br/>
            <div className='container'>
                <p className='text-secondary'>Ready</p>
            </div>
            <br/><br/><br/><br/>
            <div className='container'>
                <div className='grid'>
                    <div className="row">
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                        {Darray.map((val,index)=><Datacircle key={index} eval={val} Id={index}/>)}
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                    </div><br/><br/><br/><br/>
                <div className="row">
                    <div className="col-4"></div>
                    {/* <Restartbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-secondary' onClick={()=>{
                            var da=[];
                            for(var i=0;i<6;i++){
                                da[i]=(Math.floor((Math.random()*100)+1));
                            }
                            update(da);
                        }
                    }>reset</button>
                </div>
                    {/* <Startbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-primary' onClick={()=>{
                            var da=Darray;
                            // console.log(da);
                            for(var i=0;i<da.length;i++){
                                let temp=0;
                                // console.log(da);
                                for(var j=1;j<da.length-i;j++){
                                    // console.log(da);
                                    if(da[j-1]>da[j]) { 
                                        temp=da[j];
                                        da[j]=da[j-1];
                                        da[j-1]=temp;
                                        // setArray(da); 
                                    }
                                    // console.log(da);
                                    // alert(da);
                                    update(da);
                                }
                                // setArray(da);
                            }
                            // setArray(da);
                        }}    
                    >start</button>
                    </div>
                    <Pausebtn/>
                    <div className="col"></div>
                    
                    </div>
                </div>
            </div>
            <div className='footer'>

            </div>
        </div>
    );
}
export default Bubblesort;
