import React, { useState } from 'react';
import vlogo from './vlogo.jpg';
import Navlistarray from './Navlistarray';
import Navlist from './Navlist';
import Datacircle from './Datacircle';
// import Darray from './Darray';
import Restartbtn from './Restartbtn';
import Startbtn from './Startbtn';
import Pausebtn from './Pausebtn';
import './index.css'
// var Darray=[];
function App(){
  const { useState } = React;
  var [Darray,setnewarray]=useState([]);
  // setnewarray(Darray => [...Darray, 0]);
  for(var i=0;i<2;i++){
    Darray.push(0);
    // alert(Darray);
  }
  return(
    <div>
      <nav className="navbar navbar-expand-md navbar-dark bg-dark">
        <div className="container">
          <h1 className="navbar-brand"><img src={vlogo} className="w-25 rounded"></img>Visualiser</h1>
          <button className="navbar-toggler" data-bs-toggle="collapse" data-bs-target="#navbaraid">
            <span className="navbar-toggler-icon" ></span>
          </button>
          <div className="collapse navbar-collapse" id="navbaraid">
            <ul className="navbar-nav text-center ms-auto">
              {Navlistarray.map((val)=><Navlist eval={val}/>)}
            </ul>
          </div>
        </div>
      </nav>
      <br/>
      <div className='container'>
        <p className='text-secondary'>Ready</p>
      </div>
      <br/><br/><br/><br/>
      <div className='container-fluid'>
        <div className='grid'>
          <div className="row">
            <div className='col'>
              <h1 className='grid-item'> </h1>
            </div>
            {Darray.map((val)=><Datacircle eval={val}/>)}
            <div className='col'>
              <h1 className='grid-item'> </h1>
            </div>
          </div><br/><br/><br/><br/>
          <div className="row">
            <div className="col-4">
            </div>
            <Restartbtn Darray={Darray}/>
            <Startbtn/>
            <Pausebtn/>
            <div className="col">
            </div>

          </div>
        </div>
      </div>
      <div className='footer'>

      </div>
    </div>
  );
}
export default App;
// export {Darray};
