import { array } from 'prop-types';
import React, {useState,useEffect,useRef } from 'react';
import './index.css'


function Mergesort(){
    var [time,settime]=useState(20);
    var [state,setstate]=useState('Unset');
    var inputRef=array;
    inputRef[0] = useRef()
    inputRef[1] = useRef()
    inputRef[2] = useRef()
    inputRef[3] = useRef()
    inputRef[4] = useRef()
    inputRef[5] = useRef()
    var [Darray,setArray]=useState([{
        id:0,
        data:0,
        bgclr:'white',
    },
    {
        id:1,
        data:0,
        bgclr:'white',
    },
    {
        id:2,
        data:0,
        bgclr:'white',
    },
    {
        id:3,
        data:0,
        bgclr:'white',
    },
    {
        id:4,
        data:0,
        bgclr:'white',
    },
    {
        id:5,
        data:0,
        bgclr:'white',
    }]);

    
    function setval(id,val,clr){
        setArray((preValue)=>{
            if(id===0){
            return [
                    {data:val,bgclr:clr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===1){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:val,bgclr:clr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===2){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:val,bgclr:clr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===3){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:val,bgclr:clr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]

            }
            else if(id===4){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:val,bgclr:clr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===5){
            return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
