import { array } from 'prop-types';
import React, {useState,useRef } from 'react';
import './index.css'


function Selectionsort(){
    var [time,settime]=useState(1000);
    var [state,setstate]=useState('Unset');
    var inputRef=array;
    inputRef[0] = useRef()
    inputRef[1] = useRef()
    inputRef[2] = useRef()
    inputRef[3] = useRef()
    inputRef[4] = useRef()
    inputRef[5] = useRef()
    var [Darray,setArray]=useState([{
        id:0,
        data:0,
        bgclr:'white',
    },
    {
        id:1,
        data:0,
        bgclr:'white',
    },
    {
        id:2,
        data:0,
        bgclr:'white',
    },
    {
        id:3,
        data:0,
        bgclr:'white',
    },
    {
        id:4,
        data:0,
        bgclr:'white',
    },
    {
        id:5,
        data:0,
        bgclr:'white',
    }]);

    
    function setval(id,val,clr){
        setArray((preValue)=>{
            if(id===0){
            return [
                    {data:val,bgclr:clr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===1){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:val,bgclr:clr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===2){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:val,bgclr:clr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===3){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:val,bgclr:clr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]

            }
            else if(id===4){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:val,bgclr:clr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===5){
            return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:val,bgclr:clr,id:preValue[5].id}
                ]
            }
        })
    }
    function sleep(ms){
        return new Promise(resolve=>setTimeout(resolve,ms));
    }    
    return(
        <div>
            <div className='container'>
                <div>
                    <p className='text-secondary' style={{float:'left'}}>{state}</p>
                    <h1 style={{float:'inherit',textAlign:'center'}} className=''>SELECTION SORT<small className='font-weight-normal font-size-small' style={{float:'right',fontSize:"16px",fontWeight:'normal'}}>
                    Speed: <select id="std" onChange={(event)=>
                {   var x=document.getElementById('std').value;
                    settime(x);
                    }}>

<option id="ov3"  value="1000">Fast</option>

<option id="ov5"  value="2000">Normal</option>
<option  id="ov6" value="4000">Slow</option>
</select></small></h1></div>
                
            </div>
            <br/><br/><br/><br/>
            <div className='container'>
                <div className='grid'>
                    <div className="row">
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                        {Darray.map((value,index)=>{
                            return (<div key={value.id} className='col'>
                            <h1 className='rounded-circle text-center border border-secondary' 
                            ref={inputRef[value.id]}
                            style={{backgroundColor:value.bgclr,position:'relative',width:'75px'}}>{value.data}</h1>
                            </div>);})
                        }
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                    </div><br/><br/><br/><br/>
                <div className="row">
                    <div className="col-3"></div>
                        {/* <Resetbtn/> */}
                        <div className="col">
                        <button id='reset' disabled={false} className='btn btn-outline-secondary' onClick={async()=>{
                            setstate('Setting...');
                            document.getElementById('reset').disabled=true;
                            document.getElementById('start').disabled=true;
                            document.getElementById('reload').disabled=true;
                            document.getElementById('std').disabled=true;
                            var da=[];
                            var i=0;
                            var id=setInterval(func,150);
                            function func(){
                                if(i>4){
                                    clearInterval(id);
                                }
                                da[i]=(Math.floor((Math.random()*100)+1));
                                setval(i,da[i],"lightgrey");
                                i++;
                            }
                            setstate('Ready');
                            document.getElementById('reset').disabled=false;
                            document.getElementById('start').disabled=false;
                            document.getElementById('reload').disabled=false;
                            document.getElementById('std').disabled=false;
                        } 
                    }>reset</button>
                    </div>
                        {/* <Startbtn/> */}
                        <div className="col">
                        <button id='start' className='btn btn-outline-primary' onClick={async()=>{
                            setstate('Sorting...');
                            document.getElementById('reset').disabled=true;
                            document.getElementById('start').disabled=true;
                            document.getElementById('reload').disabled=true;
                            document.getElementById('std').disabled=true;
                            var da=[Darray[0].data,Darray[1].data,Darray[2].data,Darray[3].data,Darray[4].data,Darray[5].data];
                            var i=0;
                            var id=setInterval(func1,150);
                            function func1(){
                                if(i>4){
                                    clearInterval(id);
                                }
                                setval(i,da[i],"lightblue");
                                i++;
                                return
                            }
                            
                            for(var k=0;k<da.length;k++){
                                var r=k;
                                var min=da[k];
                                var j=k+1;
                                await sleep(time);
                                setTimeout(func0(),time);
                                        function func0(){
                                            setval(r,da[r],"orange");
                                            return
                                        }
                                await sleep(time);

                                while(j<=5){
                                    // await sleep(time);
                                    setTimeout(func01(),time);
                                        function func01(){
                                                setval(j,da[j],"royalblue");
                                                return        
                                        }
                                        await sleep(time);
                                    setTimeout(func001(),time);
                                        function func001(){
                                                setval(j,da[j],"royalblue");
                                                return        
                                        }
                                    
                                    if(da[j]<min){
                                        
                                        setTimeout(func2(),time);
                                        function func2(){
                                            
                                            setval(j,da[j],"orange");
                                            setval(r,da[r],"lightblue");
                                            min=da[j];
                                            r=j;
                                            return
                                        }
                                    await sleep(time);

                                    }
                                    else{
                                        setTimeout(func02(),time);
                                        function func02(){
                                            
                                            setval(j,da[j],"lightblue");
                                            return
                                        }
                                    }
                                    
                                    j++;
                                }
                                // await sleep(time);
                                setTimeout(func03(),time);
                                function func03(){
                                    setval(k,da[k],"royalblue");
                                    return        
                                }
                                await sleep(time);
                                setTimeout(func0003(),time);
                                function func0003(){
                                    setval(k,da[k],"royalblue");
                                    return        
                                }

                                if(k==r){
                                    setTimeout(func3(),time);
                                    function func3(){
                                        setval(k,da[k],"lightgreen");
                                        return;        
                                    }
                                }
                                else{
                                    var temp=da[k];
                                    inputRef[r].current.animate([
                                        {opacity:1},
                                        {transform:'rotate(180deg)'},
                                        {opacity:0},
                                        {transform:'rotate(180deg,360deg)'},
                                        {opacity:1}
                                    ],
                                    {duration:1000
                                    });
                                                                                     
                                                
                                    inputRef[k].current.animate([
                                        {opacity:1},
                                        {transform:'rotate(180deg)'},
                                        {opacity:0},
                                        {transform:'rotate(180deg,360deg)'},
                                        {opacity:1}



                                    ],
                                    {duration:1000
                                    });
                                    // await sleep(time);
                                    setTimeout(func003(),time);
                                    function func003(){
                                        
                                        setval(k,da[k],"Coral");
                                        return;        
                                    }
                                    setTimeout(func03(),time);
                                    function func03(){
                                        setval(r,da[r],"Coral");
                                        return        
                                    }
                                    da[k]=da[r];
                                        da[r]=temp;
                                // setTimeout(setval,1000,r,da[r],"lightblue");
                                // setTimeout(setval,1000,r,da[r],"lightblue");


                                }
                                setTimeout(setval,500,r,da[r],"lightblue");
                                
                                setTimeout(setval,500,k,da[k],"lightgreen");
                                
                            }
                            setTimeout(setval,1000,5,da[5],"lightgreen");
                            setstate('Sorted!!')
                            document.getElementById('reset').disabled=false;
                            document.getElementById('start').disabled=false;
                            document.getElementById('reload').disabled=false;
                            document.getElementById('std').disabled=false;
                        }
                    }    
                    >start</button>
                    </div>
                        {/* <Reloadbtn/> */}
                        <div className="col-4">
                        <button id='reload' className='btn btn-outline-warning' onClick={()=>{
                            window.location.reload();
                            }}>reload</button>
                        </div>
                    </div>
                </div>
            </div>
            {/* <div className='footer'>

            </div> */}
        </div>
    );
}
export default Selectionsort;
