import React from 'react';
import Darray from './Darray';
import Datacircle from './Datacircle';
// function da(){
//     for(var i=0;i<6;i++){
//         Darray[i]=Math.floor((Math.random()*100)+1); 
//     }alert(Darray)
//     return Darray;
// }
function Restartbtn(){
    return(
        <div className="col">
            <button className='btn btn-outline-secondary' onClick={()=>{
                for(var i=0;i<6;i++){
                    Darray[i]=Math.floor((Math.random()*100)+1); 
                }
                setState({Darray});
                <Datacircle/>
            }}>reset</button>
        </div>
    );
}
export default Restartbtn;
