import React, { useEffect, useState } from 'react';
import Datacircle from './Datacircle';
import Navbar from './Navbar';
import Pausebtn from './Pausebtn';
import './index.css'


function Bubblesort(){
    // var da=[];
    var [bgh0,seth0color]=useState('white')
    var [bgh1,seth1color]=useState('white')
    var [bgh2,seth2color]=useState('white')
    var [bgh3,seth3color]=useState('white')
    var [bgh4,seth4color]=useState('white')
    var [bgh0,seth0color]=useState('white')
    var [Darray,setArray]=useState([0,0,0,0,0,0]);
    
    // var update=(da)=>{
    //     var interval=setInterval(()=>{
    //         setArray(da);
    //         console.log(da);
    //     },1000);
    //     return()=>clearInterval(interval);
    // }
    // useEffect(()=>{
    //     console.log(Darray);
    // });
    return(
        <div>
            <Navbar/>
            <br/>
            <div className='container'>
                <p className='text-secondary'>Ready</p>
            </div>
            <br/><br/><br/><br/>
            <div className='container'>
                <div className='grid'>
                    <div className="row">
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                        <div className='col'>
                        <h1 className='rounded-circle text-center border border-secondary' style={{backgroundColor:bgh0}} >{Darray[0]}</h1>
                        </div>
                        <div className='col'>
                        <h1 className='rounded-circle text-center border border-secondary' style={{backgroundColor:bgh1}} >{Darray[1]}</h1>
                        </div><div className='col'>
                        <h1 className='rounded-circle text-center border border-secondary' style={{backgroundColor:bgh2}} >{Darray[2]}</h1>
                        </div><div className='col'>
                        <h1 className='rounded-circle text-center border border-secondary' style={{backgroundColor:bgh3}} >{Darray[3]}</h1>
                        </div><div className='col'>
                        <h1 className='rounded-circle text-center border border-secondary' style={{backgroundColor:bgh4}} >{Darray[4]}</h1>
                        </div><div className='col'>
                        <h1 className='rounded-circle text-center border border-secondary' style={{backgroundColor:bgh5}} >{Darray[5]}</h1>
                        </div>
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                    </div><br/><br/><br/><br/>
                <div className="row">
                    <div className="col-4"></div>
                    {/* <Restartbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-secondary' onClick={()=>{
                            var da=[];
                            for(var i=0;i<6;i++){
                                da[i]=(Math.floor((Math.random()*100)+1));
                            }
                            setArray(da);
                        }
                    }>reset</button>
                </div>
                    {/* <Startbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-primary' onClick={()=>{
                            var da=Darray;
                            for(var i=0;i<da.length;i++){
                                let temp=0;
                                for(var j=1;j<da.length-i;j++){
                                    if(da[j-1]>da[j]) { 
                                        temp=da[j];
                                        da[j]=da[j-1];
                                        da[j-1]=temp;
                                    }
                                    // update(da);
                                    setArray(da);
                                    console.log(Darray);
                                     // console.log(da);
                                    // console.log(i);
                                    // console.log(j);
                                }
                            }
                        }}    
                    >start</button>
                    </div>
                    <Pausebtn/>
                    <div className="col"></div>
                    
                    </div>
                </div>
            </div>
            <div className='footer'>

            </div>
        </div>
    );
}
export default Bubblesort;
