import React from 'react';
import bubble from './bubble.PNG'
import insertion from './insertion.PNG'
import selection from './selection.PNG'
import merge from './merge.PNG'

const Home=()=>{
    return(
        <>
            <h1><center>V  I  S  A  L</center></h1>
            <pre><center>Algorithm visualization platform</center></pre><br/>
            <div className='px-0'>&nbsp;
                <a href="/Bubblesort" className='mx-2'>
                    <img className='image-responsive' id='img1' src={bubble} 
                    Style='height:160px;width:250px; box-shadow:0 2px 4px 0 grey,0 2px 10px 0 grey;' 
                    onMouseOver={()=>{document.getElementById('img1').style.opacity=0.7;}}
                    onMouseOut={()=>{document.getElementById('img1').style.opacity=1;}}/>
                </a>
                <a href="/Insertionsort" className='mx-2'>
                    <img className='image-responsive' id='img2' src={insertion} 
                    Style='height:160px;width:250px; box-shadow:0 2px 4px 0 grey,0 2px 10px 0 grey;' 
                    onMouseOver={()=>{document.getElementById('img2').style.opacity=0.7;}}
                    onMouseOut={()=>{document.getElementById('img2').style.opacity=1;}}/>
                </a>
                <a href="/Selectionsort" className='mx-2'>
                    <img className='image-responsive' id='img3' src={selection} 
                    Style='height:160px;width:250px; box-shadow:0 2px 4px 0 grey,0 2px 10px 0 grey;' 
                    onMouseOver={()=>{document.getElementById('img3').style.opacity=0.7;}}
                    onMouseOut={()=>{document.getElementById('img3').style.opacity=1;}}/>
                </a>
                <a href="/Mergesort" className='mx-2'>
                    <img className='image-responsive' id='img4' src={merge} 
                    Style='height:160px;width:250px; box-shadow:0 2px 4px 0 grey,0 2px 10px 0 grey;' 
                    onMouseOver={()=>{document.getElementById('img4').style.opacity=0.7;}}
                    onMouseOut={()=>{document.getElementById('img4').style.opacity=1;}}/>
                </a>
                
            </div>
            <br/>
            <h2><pre>    BUBBLE SORT     INSERTION SORT    SELECTION SORT      MERGE SORT</pre></h2>
        </>
    );
}
export default Home;
