import React, {useState } from 'react';
// import Datacircle from './Datacircle';
// import Navbar from './Navbar';
import Pausebtn from './Pausebtn';
import './index.css'


function Bubblesort(){
    // var da=[];
    var [Darray,setArray]=useState([{
        id:0,
        data:0,
        bgclr:'white',
    },
    {
        id:1,
        data:0,
        bgclr:'white',
    },
    {
        id:2,
        data:0,
        bgclr:'white',
    },
    {
        id:3,
        data:0,
        bgclr:'white',
    },
    {
        id:4,
        data:0,
        bgclr:'white',
    },
    {
        id:5,
        data:0,
        bgclr:'white',
    }]);
    
    function setval(id,val,clr){
        setArray((preValue)=>{
            if(id===0){
            console.log(preValue[0]);
            return [
                    {data:val,bgclr:clr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===1){
            console.log(preValue[0]);
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:val,bgclr:clr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===2){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:val,bgclr:clr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===3){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:val,bgclr:clr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]

            }
            else if(id===4){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:val,bgclr:clr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===5){
            console.log(preValue);
            return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:val,bgclr:clr,id:preValue[5].id}
                ]
            }
            console.log(preValue);
        })
    }
    // var [bgh0,seth0color]=useState('white')
    // var [bgh1,seth1color]=useState('white')
    // var [bgh2,seth2color]=useState('white')
    // var [bgh3,seth3color]=useState('white')
    // var [bgh4,seth4color]=useState('white')
    // var [bgh5,seth5color]=useState('white')
    
    
    // var update=(da)=>{
    //     var interval=setInterval(()=>{
    //         setArray(da);
    //         console.log(da);
    //     },1000);
    //     return()=>clearInterval(interval);
    // }
    // useEffect(()=>{
    //     console.log(Darray);
    // });
    return(
        <div>
            
            <div className='container'>
                <p className='text-secondary'>Ready</p>
            </div>
            <br/><br/><br/><br/>
            <div className='container'>
                <div className='grid'>
                    <div className="row">
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                        {Darray.map((value,index)=>{
                            // console.log(index); 
                            // console.log(value); 
                            return (<div key={value.id} className='col'>
                            <h1 className='rounded-circle text-center border border-secondary' 
                            id={value.id}
                            style={{backgroundColor:value.bgclr}}>{value.data}</h1>
                            </div>);})
                        }
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                    </div><br/><br/><br/><br/>
                <div className="row">
                    <div className="col-4"></div>
                    {/* <Restartbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-secondary' onClick={()=>{
                            var da=[];
                            for(var i=0;i<6;i++){
                                da[i]=(Math.floor((Math.random()*100)+1));
                                setval(i,da[i],"lightblue");
                            }
                        }
                    }>reset</button>
                </div>
                    {/* <Startbtn/> */}
                    <div className="col">
                        <button className='btn btn-outline-primary' onClick={()=>{
                            var da=Darray;
                            for(var i=0;i<da.length;i++){
                                let temp=0;
                                for(var j=1;j<da.length-i;j++){
                                    if(da[j-1]>da[j]) { 
                                        temp=da[j];
                                        da[j]=da[j-1];
                                        da[j-1]=temp;
                                    }
                                    // update(da);
                                    setArray(da);
                                    console.log(Darray);
                                    // window.location.reload(false);
                                     // console.log(da);
                                    // console.log(i);
                                    // console.log(j);
                                }
                            }
                        }}    
                    >start</button>
                    </div>
                    <Pausebtn/>
                    <div className="col"></div>
                    
                    </div>
                </div>
            </div>
            <div className='footer'>

            </div>
        </div>
    );
}
export default Bubblesort;
