import { array } from 'prop-types';
import React, {useState,useEffect,useRef } from 'react';
import './index.css'


function Mergesort(){
    var [time,settime]=useState(20);
    var [state,setstate]=useState('Unset');
    var inputRef=array;
    inputRef[0] = useRef()
    inputRef[1] = useRef()
    inputRef[2] = useRef()
    inputRef[3] = useRef()
    inputRef[4] = useRef()
    inputRef[5] = useRef()
    var [Darray,setArray]=useState([{
        id:0,
        data:0,
        bgclr:'white',
    },
    {
        id:1,
        data:0,
        bgclr:'white',
    },
    {
        id:2,
        data:0,
        bgclr:'white',
    },
    {
        id:3,
        data:0,
        bgclr:'white',
    },
    {
        id:4,
        data:0,
        bgclr:'white',
    },
    {
        id:5,
        data:0,
        bgclr:'white',
    }]);

    
    function setval(id,val,clr){
        setArray((preValue)=>{
            if(id===0){
            return [
                    {data:val,bgclr:clr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===1){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:val,bgclr:clr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===2){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:val,bgclr:clr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===3){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:val,bgclr:clr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]

            }
            else if(id===4){
                return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:val,bgclr:clr,id:preValue[4].id},
                    {data:preValue[5].data,bgclr:preValue[5].bgclr,id:preValue[5].id}
                ]
            }
            else if(id===5){
            return [
                    {data:preValue[0].data,bgclr:preValue[0].bgclr,id:preValue[0].id},
                    {data:preValue[1].data,bgclr:preValue[1].bgclr,id:preValue[1].id},
                    {data:preValue[2].data,bgclr:preValue[2].bgclr,id:preValue[2].id},
                    {data:preValue[3].data,bgclr:preValue[3].bgclr,id:preValue[3].id},
                    {data:preValue[4].data,bgclr:preValue[4].bgclr,id:preValue[4].id},
                    {data:val,bgclr:clr,id:preValue[5].id}
                ]
            }
        })
    }
    function sleep(ms){
        return new Promise(resolve=>setTimeout(resolve,ms));
    }    
    return(
        <div>
            <div className='container'>
                <div><p className='text-secondary' style={{float:'left'}}>{state}</p><h1 style={{float:'inherit',textAlign:'center'}} className=''>Merge Sort<small className='font-weight-normal font-size-small' style={{float:'right',fontSize:"16px",fontWeight:'normal'}}>
                    Speed: <select id="std" onChange={(event)=>
                {   var x=document.getElementById('std').value;
                    settime(x);
                    }}>
<option id="ov1"  value="20" >0 sec</option>
<option id="ov2"  value="500" >0.5 sec</option>
<option id="ov3"  value="1000">1 sec</option>
<option id="ov4"  value="1500">1.5 sec</option>
<option id="ov5"  value="2000">2 sec</option>
<option  id="ov6" value="4000">4 sec</option>
</select></small></h1></div>
                
            </div>
            <br/><br/><br/>
            <div className='container'>
                <div className='grid'>
                    <div className="row">
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                        {Darray.map((value,index)=>{
                            return (<div key={value.id} className='col'>
                            <h1 className='rounded-circle text-center border border-secondary' 
                            ref={inputRef[value.id]}
                            style={{backgroundColor:value.bgclr,position:'relative',width:'75px'}}>{value.data}</h1>
                            </div>);})
                        }
                        <div className='col'>
                            <h1 className='grid-item'> </h1>
                        </div>
                    </div><br/><br/><br/><br/>
                <div className="row">
                    <div className="col-3"></div>
                        {/* <Resetbtn/> */}
                        <div className="col">
                        <button id='reset' disabled={false} className='btn btn-outline-secondary' onClick={async()=>{
                            setstate('Setting...');
                            document.getElementById('reset').disabled=true;
                            document.getElementById('start').disabled=true;
                            document.getElementById('reload').disabled=true;
                            document.getElementById('std').disabled=true;
                            var da=[];
                            var i=0;
                            var id=setInterval(func,150);
                            function func(){
                                if(i>4){
                                    clearInterval(id);
                                }
                                da[i]=(Math.floor((Math.random()*100)+1));
                                setval(i,da[i],"lightgrey");
                                i++;
                            }
                            setstate('Ready');
                            document.getElementById('reset').disabled=false;
                            document.getElementById('start').disabled=false;
                            document.getElementById('reload').disabled=false;
                            document.getElementById('std').disabled=false;
                        }
                    }>reset</button>
                    </div>
                        {/* <Startbtn/> */}
                        <div className="col">
                        <button id='start' className='btn btn-outline-primary' onClick={async()=>{
                            setstate('Sorting...');
                            document.getElementById('reset').disabled=true;
                            document.getElementById('start').disabled=true;
                            document.getElementById('reload').disabled=true;
                            document.getElementById('std').disabled=true;
                            var da=[Darray[0].data,Darray[1].data,Darray[2].data,Darray[3].data,Darray[4].data,Darray[5].data];
                            var id=setInterval(func1,150);
                            var i=0;
                                function func1(){
                                if(i>4){
                                    clearInterval(id);
                                }
                                setval(i,da[i],"lightblue");
                                i++;
                                return
                            }
                            await sleep(time);
                                var temp=[];
                                var n=da.length;
                                for  (var groupLen=1;groupLen<n;groupLen=groupLen<<1)	
                                {	console.write()
                                    	
                                    var resCount=0;//The subscript of the temp array. The location where each merging result is stored in temp 		
                                    var groupCount=Math.ceil(n*1.0/groupLen); //Number of groups, if less than one group, one group 		
                                    var mergeCount=groupCount>>1;//The number of merging was once in two groups. Single group does not merge. 			
                                    //The second step is to merge the two groups.		
                                    for (var i = 0;i<mergeCount;i++)		
                                    {
                                        await sleep(time);
                                    		
                                        var left = (i*groupLen)<<1; //Start position of left group 			
                                        var leftSide=left+groupLen; //The end of the left group.			
                                        var right=leftSide;  //Start position of right group 			
                                        var rightSide=(n<right+groupLen)?n:right+groupLen; 	//The end of the right group.						
                                        //Merge two arrays. Merging can encapsulate an inline function without affecting performance. This is the benefit of non recursion. Recursion cannot inline. 			
                                        
                                        while (left<leftSide&&right<rightSide) 			
                                        {	//Merge: put the smaller one of the two group headers into a temporary array.					
                                            setTimeout(func0(),time);
                                            function func0(){
                                                setval(left,da[left],"orange");
                                                return
                                            }
                                            setTimeout(func1(),time);
                                            function func1(){
                                                setval(right,da[right],"blue");
                                                return
                                            }
                                            if(da[left]<=da[right]) //If you use da [left] < da [right], it is unstable.						
                                            temp[resCount++]=da[left++];						
                                        else		temp[resCount++]=da[right++];				
                                        }    		
                                        while (left<leftSide) //The rest of the left group is copied to the temporary array 					
                                            temp[resCount++]=da[left++];
                                            			
                                        while (right<rightSide) //Copy the rest of the right group to the temporary array 				
                                            temp[resCount++]=da[right++];		    
                                        for (left = i<<1; left < rightSide;left++) //Copy the merging result data of the temporary array back to the original array. 		        
                                            da[left] = temp[left];    	
                                    }
                                }
                            document.write(da);                            
                            setstate('Sorted!!')
                            document.getElementById('reset').disabled=false;
                            document.getElementById('start').disabled=false;
                            document.getElementById('reload').disabled=false;
                            document.getElementById('std').disabled=false;
                        }
                    }
                    >start</button>
                    </div>
                        {/* <Reloadbtn/> */}
                        <div className="col-4">
                        <button id='reload' className='btn btn-outline-warning' onClick={()=>{
                            window.location.reload();
                            }}>reload</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className='footer'>

            </div>
        </div>
    );
}
export default Mergesort;
